import React from 'react';

import './card-style.css'
import img1 from '../assets/m.png'
import Flippy, { FrontSide, BackSide } from 'react-flippy';

const Card= props=>{
  var Blur = require('react-blur');
    return(
        <Flippy
        flipOnHover={true} // default false
        flipDirection="horizontal" // horizontal or vertical
         // to use toggle method like this.flippy.toggle()
        // if you pass isFlipped prop component will be controlled component.
        // and other props, which will go to div
        /// these are optional style, it is not necessary
        
      >
        
        <FrontSide className='a' style={{
        cursor: 'pointer',
        display: 'flex',
        alignItems: 'center',
        flexDirection: 'column',
        transition : ' 2s ,  1s ',
        width:'min-content',
        boxShadow: 'none',
        backgroudColor:'red'
        
      }}>
            
            <div className="card text-center" >
            <div className="overflow">
                <img src={props.imgfront} alt="image1" className="card-img-top"></img>
            </div>
            
        </div>                    
        </FrontSide>
       
        <BackSide style={{
        cursor: 'pointer',
        display: 'flex',
        alignItems: 'center',
        flexDirection: 'column',
        transition : ' 2s ,  1s ',
        width:'min-content',
        boxShadow: "none",
      
        
      }}>
       <div className="card text-center" style={{
        //  backgroundImage:'url("https://th.bing.com/th/id/OIP.09nf33lyzYY0a2El6n1oeAHaLF?w=200&h=299&c=7&o=5&pid=1.7")',
        //  blurRadius:'1',
        
       }}>
         <Blur img={img1} blurRadius={5} enableStyles>
  The content.
</Blur>
            <div className="overflow">
                <h1>{props.title}</h1>
                <h2>{props.description}</h2>
                <h3>{props.price}</h3>
            </div>
            
        </div>                    
        </BackSide>
        
      </Flippy>
    );
}
        
        
 

export default Card;
