import React from 'react';
import img1 from '../assets/m.jpg';




const Card= props=>{
    return(
        // <div className="card text-center">
        //     <div className="overflow">
        //         <img src={img1}alt="image1" className="card-img-top"></img>
        //     </div>
        //     <div className="card-body text-dark">
        //         <h4 className="card-title">Card Title</h4>
        //         <p className="card-text text-secondary">
        //             Lorem ipsum, dolor sit amet consectetur adipisicing elit. Enim exercitationem vitae reiciendis dolor aperiam, atque voluptas sit fugiat voluptatem natus!
        //         </p>
        //         <a href="#" className="btn btn-outline-success"> Go Anywhere</a>
        //     </div>
        // </div>
        <Container>
      <Content>
        <ThreeColumn>
          <HeadingColumn textOnLeft={textOnLeft}>
            <HeadingInfoContainer>
              <Subheading>{subheading}</Subheading>
              <HeadingTitle>{headingHtmlComponent}</HeadingTitle>
              <HeadingDescription>{description}</HeadingDescription>
              <PrimaryLink>
                {linkText} <ArrowRightIcon />
              </PrimaryLink>
            </HeadingInfoContainer>
          </HeadingColumn>
          {cards.map((card, index) => (
            <CardColumn key={index}>
              <Card>
                <CardImage imageSrc={img1}/>
                <CardText>
                  <CardHeader>
                    <CardCompany>{card.company}</CardCompany>
                    <CardType>{card.type}</CardType>
                  </CardHeader>
                  <CardTitle>{card.title}</CardTitle>
                  <CardMeta>
                    <CardMetaFeature>
                      <TimeIcon /> {card.durationText}
                    </CardMetaFeature>
                    <CardMetaFeature>
                      <LocationIcon /> {card.locationText}
                    </CardMetaFeature>
                  </CardMeta>
                  <CardAction>{cardLinkText}</CardAction>
                </CardText>
              </Card>
            </CardColumn>
          ))}
        </ThreeColumn>
      </Content>
    </Container>
    );
}

export default Card;
