import React, { Component } from "react"
import Card from './CardUI'
import img1 from '../assets/m.jpg'
import img2 from '../assets/me.png'
import img3 from '../assets/me2.jpg'
import Flippy, { FrontSide, BackSide } from 'react-flippy';


class Cards extends Component{
    
    render(){
        return(
            <div className="container-fluid d-flex justify-content-center">
                <div className="row">

                    <div className="col-md-4">
                        <Card imgsrc={img1} title="ABDOCASILLAS"/>
                    </div>
                    <div className="col-md-4">
                        <Card imgsrc={img3} title="NARUTO UZUMAKI"/>
                    </div>
                    <div className="col-md-4">
                        <Card imgsrc={img3} title="CRISTIANO RONALDO"/>
                    </div>
                    
        <Flippy
        flipOnHover={true} // default false
        flipOnClick={false} // default false
        flipDirection="horizontal" // horizontal or vertical
        ref={(r) => this.flippy = r} // to use toggle method like this.flippy.toggle()
        // if you pass isFlipped prop component will be controlled component.
        // and other props, which will go to div
        style={{ width: '200px', height: '200px' }} /// these are optional style, it is not necessary
      >
      </Flippy>
                </div>
            </div>
            
        );
        
    }
}
export default Cards;
