import React, { Component } from "react"
import Card from './CardUI'
import img1 from '../assets/m.jpg'
import img2 from '../assets/me.png'
import img3 from '../assets/me2.jpg'



class Cards extends Component{
    
    render(){
        return(
            <div className="container-fluid d-flex justify-content-center">
                <div className="row">

                    <div className="col-md-4">
                        <Card imgsrc={img1} title="ABDOCASILLAS"/>
                    </div>
                    <div className="col-md-4">
                        <Card imgsrc={img3} title="NARUTO UZUMAKI"/>
                    </div>
                    <div className="col-md-4">
                        <Card imgsrc={img3} title="CRISTIANO RONALDO"/>
                    </div>
                    const FlippyOnHover = ({ flipDirection = 'vertical' }) => (
  <Flippy
    flipOnHover={true}
    flipDirection={flipDirection}
    style={FlippyStyle}
  >
    <DefaultCardContents>
    I flip {flipDirection}ly on hover
    </DefaultCardContents>
  </Flippy>
);
                </div>
            </div>
        );
    }
}
export default Cards;
