import React from 'react';
import GoogleFontLoader from 'react-google-font-loader';
import './card-style.css'
import img1 from '../assets/m.png'
import Flippy, { FrontSide, BackSide } from 'react-flippy';
import Blur from 'react-blur'
const font =  "'Quicksand', sans-serif";
const Card= props=>{
  
    return(
      
        <Flippy
        flipOnHover={true} // default false
        flipDirection="horizontal" // horizontal or vertical
         // to use toggle method like this.flippy.toggle()
        // if you pass isFlipped prop component will be controlled component.
        // and other props, which will go to div
        /// these are optional style, it is not necessary
        
      >
        
        <FrontSide className='a' style={{
        cursor: 'pointer',
        display: 'flex',
        alignItems: 'center',
        flexDirection: 'column',
        transition : ' 2s ,  1s ',
        width:'min-content',
        boxShadow: 'none',
        backgroudColor:'red'
        
      }}>
            
            <div className="card text-center" >
            <div className="overflow">
                <img src={props.imgfront} alt="image1" className="card-img-top"></img>
            </div>
            
        </div>                    
        </FrontSide>
       
        <BackSide style={{
        cursor: 'pointer',
        display: 'flex',
        alignItems: 'center',
        flexDirection: 'column',
        transition : ' 2s ,  1s ',
        width:'min-content',
        boxShadow: "none",
        fontFamilly:{font}
      
        
      }}>
       <div className="card text-center" style={{
         backgroundImage: `url(${props.imgfront})`,
         backgroundPosition: 'center',
  backgroundSize: 'cover',
  backgroundRepeat: 'no-repeat',
  
  

           //backgroundColor:'red',
           
          // blurRadius:'5',
          // filter: 'blur(3px)'
       }}>
         
            <div className="overflow"style={{backdropFilter:'blur(8px)',
          width:'100%',
          height:'91%',
          padding:'10px',
          textAlign:'left',
          color:'red',
          
          
          
          }}>
              
              <div >
                
               <h1 className="title" style={{fontSize:'10px'}}>{props.title}</h1>
                <h3 className="description">{props.description}</h3>
                <h3 className="">{props.Language}</h3>
                <h3 className="">{props.Category}</h3>
                <h3 className="">{props.Release_Date}</h3>
                
            </div>
            
            </div>
            <div className="card-footer" style={{
                  backgroundColor:'red',
                  position:'relative'
                }}>sfasfasf</div>
        </div>                    
        </BackSide>
        
      </Flippy>
    );
}
        
        
 

export default Card;
