import { Component } from 'react';
import './App.css';

import PostList from './posts/PostList'
import { render } from '@testing-library/react';

class App extends Component {

  constructor(props){
    super(props);
    this.state={
      items:[],
      isLoaded : false,
    }
  }
  //https://jsonplaceholder.typicode.com/users
  componentDidMount(){
    fetch('http://localhost:3000/posts')
    .then(res=>res.json())
    .then(json=>{
      this.setState({
        isLoaded : true,
        items : json,
      })
    })
  }









  render() {
    var {isLoaded ,items }=this.state;
    if(!isLoaded){
      return <div>is Loading.......</div>
    }
    else{
      return(
        <div className="App">
          <ul>
            {items.map(items=>(
              <li key={items._id}>
                {items.title}
              </li>

            ))}
          </ul>
        </div>
      )
    }

  }
  }



export default App;
