import './App.css';

import PostList from './posts/PostList'
import { render } from '@testing-library/react';

class App extends Component {

  constructor(props){
    super(props);
    this.state={
      items:[],
      isLoaded : false,
    }
  }
  //https://jsonplaceholder.typicode.com/users
  componentDidMount(){
    fetch('https://jsonplaceholder.typicode.com/users')
    .then(res=>res.json())
    .then(json=>{
      this.setState({
        isLoaded : true,
        items : json,
      })
    })
  }









